import './style/header.css'
import useProductQuantity from './useProductQuantity';
import Logo from './img/Logo.svg'
import Basket from './img/Basket.svg'
import menu from './img/menu.svg'

const Header = (props) => {
    const [productQuantity, incrementProductQuantity] = useProductQuantity();

    const handleIncrement = () => {
        incrementProductQuantity();

        const numberProductElement = document.querySelector('.number_product');
        if (productQuantity <= 0) {
            numberProductElement.style.display = 'none';
        } else {
            numberProductElement.style.display = 'block';
        }
    };

    return (
        <nav className="container navigation">
            <div className = "logo">
                <img src={Logo} alt="Logo" />
            </div>
            <div className = 'tex_nav'>
                <p>Home</p>
                <p>Products</p>
                <p>Promotions</p>
            </div>
            <div>
                <div className = "parrent_basket" onClick={handleIncrement}>
                    <span className = "number_product">{productQuantity}</span>
                    <img className = "basket" src = {Basket} alt="Basket" />
                </div>
                <img className = "menu" src = {menu} alt = "Menu" />
            </div>
        </nav>
    );
}

export default Header;
