import { useState } from 'react';
import '../style/header.css'

const useProductQuantity = () => {

    const [productQuantity, setProductQuantity] = useState(0);
    const incrementProductQuantity = () => {
        setProductQuantity(productQuantity + 1);

        const whereToEnter = document.querySelector('.number_product');
        if (productQuantity <= 0) {
            whereToEnter.getElementsByClassName.display = "none";
        } else {
            whereToEnter.getElementsByClassName.display = "block";
        }
        console.log(whereToEnter);
    }

    return (
        [productQuantity, incrementProductQuantity]
    );
}

export default useProductQuantity;
